mapper.insert:
INSERT
  INTO
      TRANSPORTATION 
      (
      TR_NO
     ,TR_TYPE
     ,TR_TYPE2
     ,TR_FEE
      )
VALUES
      (
      SEQ_TR_NO.NEXTVAL
     ,#{trType}
     ,#{trType2}
     ,#{trFee}
      )

mapper.update:
UPDATE 
      PLACE
   SET
      PLACE_TYPE = #{placeType}
     ,PLACE_NAME = #{placeName}
     ,PLACE_LOCATION = #{placeLocation}
     ,PLACE_LAT = #{placeLat}
     ,PLACE_LNG = #{placeLng}
     ,PLACE_PHONE = #{placePhone}
     ,PLACE_INFO = #{placeInfo}
     ,PLACE_ORIGIN_NAME = #{placeOriginName}
     ,PLACE_CHANGE_NAME = #{placeChangeName}
WHERE
     PLACE_NO = #{placeNo}

UPDATE 
      MEMBER
   SET 
     MEM_GRADE = #{memGrade}
 WHERE 
      MEM_ID = #{memberId}
