mapper.select:
INSERT
	INTO
			BOARD
			(
			BOARD_NO
			,BOARD_TITLE
			,BOARD_CONTENT
			,BOARD_WRITER
			,ORIGIN_NAME
			,CHANGE_NAME
			,BOARD_NUM
			)
	VALUES
			(
			SEQ_BNO.NEXTVAL
			,#{boardTitle}
			,#{boardContent}
			,#{boardWriter}
			,#{originName}
			,#{changeName}
			,1
			)

mapper.update:
UPDATE
		BOARD
SET
		BOARD_TITLE = #{boardTitle}
		,BOARD_CONTENT = #{boardContent}
		,ORIGIN_NAME = #{originName}
		,CHANGE_NAME = #{changeName}
WHERE
		BOARD_NO = #{boardNo}
AND
		BOARD_NUM =1

