mapper.delete:
	DELETE FROM 
				LIKES
	WHERE 
				LIKE_NO=#{likeNo}
        AND
	        	LIKE_ID=#{likeId}

mapper.insert:
INSERT
	INTO
		COMMENTS
		(
		COMMENT_NO
		,COMMENT_TEXT
		,COMMENT_WRITER
		,BOARD_NO
		)
	VALUES
		(
		SEQ_C_NO.NEXTVAL
		,#{commentText}
		,#{commentWriter}
		,#{boardNo}
		)
