mapper.insert:
insert
  into
      cafe
      (
      cafe_no
     ,cafe_title
     ,cafe_content
     ,cafe_address
     ,cafe_service
     ,origin_name
     ,change_name
     ,lat
     ,lng
     ,cafe_status
      )
 values
 	  (
 	  seq_c_no.nextval
 	  ,#{cafeTitle}
 	  ,#{cafeContent}
 	  ,#{cafeAddress}
 	  ,#{cafeService}
 	  ,#{originName}
 	  ,#{changeName}
 	  ,#{lat}
 	  ,#{lng}
 	  ,default
 	  )

INSERT
  INTO
      CAFE_RES
     (
      CAFE_RES_NO
	 ,MEM_ID
	 ,CAFE_NO
	 ,RES_TIME
	 ,RES_DATE
	 ,CAFE_RES_STATUS
      )
 VALUES
      (
      SEQ_CR_NO.NEXTVAL
     ,#{memId}
     ,#{cafeNo}
     ,#{resTime}
     ,#{resDate}
     ,default      
      )
